mapper.select:
select * from board_report
where board_report_board_no = #{boardReportBoardNo}
order by board_report_date desc

	select count(*) from board_report
	where board_report_member_id = #{boardReportMemberId}
 	and board_report_board_no = #{boardReportBoardNo}

    select * from (
    select rownum rn, TMP.* from (
    select * from view_board_report_stats
    order by last_reported_at desc, board_no desc
            ) TMP
) where rn between #{start} and #{end}
