mapper.update:
UPDATE ICON SET 
    icon_name = #{iconName},
    icon_rarity = #{iconRarity},
    icon_category = #{iconCategory},
    icon_src = #{iconSrc}
WHERE icon_id = #{iconId}

mapper.select:
SELECT * FROM (
    SELECT TMP.*, ROWNUM RN FROM (
        SELECT * FROM ICON


                AND icon_rarity = #{type}


        ORDER BY icon_id DESC
    ) TMP
)
WHERE RN BETWEEN #{startRow} AND #{endRow}
