mapper.select:
SELECT count(*) 
FROM MEMBER_ICONS 
WHERE MEMBER_ICONS_MEMBER_ID = #{memberId} 
  AND MEMBER_ICONS_ICON_ID = #{iconId}

mapper.update:
UPDATE MEMBER_ICONS 
SET IS_EQUIPPED = 'N'
WHERE MEMBER_ICONS_MEMBER_ID = #{memberId}

UPDATE MEMBER_ICONS
SET IS_EQUIPPED = 'Y'
WHERE MEMBER_ICONS_MEMBER_ID = #{memberId}
  AND MEMBER_ICONS_ICON_ID = #{iconId}
