mapper.insert:
insert into member(
	member_id, member_pw, member_nickname,
	member_email, member_birth, member_contact,
	member_post, member_address1, member_address2
)
values(
	#{memberId},#{memberPw},#{memberNickname},
	#{memberEmail}, #{memberBirth},#{memberContact},
	#{memberPost}, #{memberAddress1}, #{memberAddress2}
)

mapper.select:
select * from (
select rownum rn, TMP.* from(
	select * from member 
	where member_level != '관리자'
	order by member_join desc
) TMP
) where rn between #{begin} and #{end}

		select * from (
select rownum rn, TMP.* from(
select * from member

	member_level != '관리자'



				and member_id LIKE '%' || #{keyword} || '%'



				and member_nickname LIKE '%' || #{keyword} || '%'



				and member_level = #{keyword}




order by member_join desc
		) TMP
) where rn between #{pageVO.begin} and #{pageVO.end}

mapper.update:
update member
set member_pw=#{memberPw}
where member_id=#{memberId}

update member
set member_nickname=#{memberNickname}
where member_id=#{memberId}

update member
set member_point=#{memberPoint}
where member_id=#{memberId}

update member
set member_reliability = member_reliability + #{rel}
where member_id=#{memberId}

update member
set member_reliability = #{rel} 
where member_id=#{memberId}

update member
set member_reliability = member_reliability - #{rel}
where member_id = #{memberId}

update member
set member_level = #{memberLevel}
where member_id = #{memberId}

UPDATE member
SET member_point = member_point + #{memberPoint}
WHERE member_id = #{memberId}

mapper.select:
SELECT * FROM ( SELECT TMP.*, ROWNUM AS rn FROM ( SELECT member_id AS "memberId", member_nickname AS "nickname", member_point AS "point", member_level AS "level", RANK() OVER (ORDER BY member_point DESC) AS "ranking" FROM member WHERE member_level != '관리자'
AND member_nickname LIKE '%' || #{keyword} || '%'
ORDER BY "ranking" ASC ) TMP ) WHERE rn BETWEEN #{start} AND #{end}
