mapper.insert:
		insert into review(
			review_no, review_contents, review_writer, review_rating,
			review_spoiler, review_text, review_like,
            review_realiability, review_price
		)
		values(
			review_seq.nextval, #{reviewContents}, #{reviewWriter}, #{reviewRating},
			 #{reviewSpoiler}, #{reviewText}, 0, 0, #{reviewPrice}
		)

mapper.select:
select A.*,  B.member_nickname, B.member_reliability 
from review A join member B on A.review_writer = B.member_id 
where review_writer = #{loginId} and review_contents = #{reviewContents}

select A.*, B.member_nickname, B.member_reliability 
from review A join member B on A.review_writer = B.member_id 
where review_contents = #{reviewContents} and review_no = #{reviewNo}

mapper.delete:
delete review where
review_contents = #{reviewContents} and
review_no = #{reviewNo}

mapper.update:
update review set review_like = (
		select count(*) from review_like where review_no = #{reviewNo}
)	where review_no = #{reviewNo}
