mapper.select:

select A.* from review A join contents B  on A.review_contents
= B.contents_id where instr(B.contents_title, #{contentsTitle}) > 0  
order by A.review_wtime desc;

mapper.update:

update review 


		review_rating = #{reviewRating},


		review_spoiler = #{reviewSpoiler},


		review_text = #{reviewText}


where review_no = #{reviewNo}
