mapper.delete:
    	delete watchlist
    	where watchlist_content=#{watchlistContent}
			and watchlist_member=#{watchlistMember}

mapper.select:
   		select count(*) from watchlist
   		where watchlist_content=#{watchlistContent}
			and watchlist_member=#{watchlistMember}

mapper.update:
   		update watchlist
   		set watchlist_type = #{watchlistType}
   		where watchlist_content=#{watchlistContent}
			and watchlist_member=#{watchlistMember}
